$userUPN = "username@contoso.com"
$aadUser = "AzureAD\$userUPN"; try {$sid = (New-Object System.Security.Principal.NTAccount($aadUser)).Translate([System.Security.Principal.SecurityIdentifier]).Value; Write-Host "User SID is: $sid";} catch { Write-Error "Could not resolve SID for $aadUser"; exit;}; $infPath = "$env:TEMP\secpol.inf"; $seceditDbPath = "$env:TEMP\secedit.sdb"; secedit /export /cfg $infPath /areas USER_RIGHTS ; $inf = Get-Content $infPath; $policyLine = "SeDenyInteractiveLogonRight"; for ($i = 0; $i -lt $inf.Count; $i++) { if ($inf[$i] -match "^$policyLine\s*=") { $currentValue = $inf[$i] -replace "$policyLine\s*=\s*", ""; $updatedSIDs = $currentValue.Split(',') | Where-Object { $_ -ne $sid }; if ($updatedSIDs.Count -eq 0) { $inf = $inf[0..($i - 1)] + $inf[($i + 1)..($inf.Count - 1)]; } else { $inf[$i] = "$policyLine = " + ($updatedSIDs -join ",");} break;}}; $inf | Set-Content $infPath -Encoding Unicode; secedit /configure /db $seceditDbPath /cfg $infPath /areas USER_RIGHTS /quiet ; Remove-Item $infPath -Force; Remove-Item $seceditDbPath -Force; Write-Host "Removed $userUPN from Deny logon locally"